import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import "../style.css";

const Modal = ({show, setShow, person}) => {
    const [player, setPlayer] = useState([]);
  const handleClose = e => {
      e.preventDefault()
      setShow(false)
  };

  useEffect (() => {

    axios.get(`https://project.trumedianetworks.com/api/nfl/player/${person}`, {
      headers: {
          'tempToken': 'd818a26a-a42d-4fc5-8ab7-44020a0dad5e',
        }
      })
      .then((res) => {
        console.log(res.data);

      if (res.data) {

        setPlayer(res.data);
      }else{
        console.log('error')
      }
    })
    .catch(error => {
      console.log('error', error);
    });

  }, [person]);

  useEffect(() => {
    document.body.addEventListener("keydown", handleClose);
    return function cleanup() {
      document.body.removeEventListener("keydown", handleClose);
    };
  }, []);
const statList = player.map(player => {

    return (
        <div className="modal">
        <div className="modal-content">
            <table>
                <thead>
                    <th>Week</th>
                    <th>Passing Yards</th>
                    <th>Comp/Att</th>
                    <th>Cmp %</th>
                    <th>Yards/Attempt</th>
                    <th>Pass TDs</th>
                    <th>Ints</th>
                    <th>Sacks</th>
                    <th>Rushing attempts</th>
                    <th>Rush Yards</th>
                    <th>Avg. Rush Yards</th>
                    <th>Rush Tds</th>
                </thead>
                <tbody>
                    {/* <td>{player.week[0]}</td>
                    <td>{player.PsYds[0]}</td>
                    <td>{player.Cmp[0]}/{player.Att[0]}</td>
                    <td>{player.Cmp[0]/player.Att[0]}</td>
                    <td>{player.PsYds[0]/player.Att[0]}</td>
                    <td>{player.PsTD[0]}</td>
                    <td>{player.Int[0]}</td>
                    <td>{player.Sack[0]}</td>
                    <td>{player.Rush[0]}</td>
                    <td>{player.RshYds[0]}</td>
                    <td>{player.RshYds[0]/player.Rush[0]}</td>
                <td>{player.RshTD[0]}</td> */}
                </tbody>
            </table>

            <button onClick={e=>handleClose(e)} className="button">
              Close
            </button>
            {person}
        </div>
      </div>
  );
});
};

export default Modal;
