import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import "../style.css";

const Modal = ({show, setShow, person}) => {
    const [player, setPlayer] = useState([]);
  const handleClose = e => {
      e.preventDefault()
      setShow(false)
  };

  useEffect (() => {
      console.log('this shit runs dog')
    axios.get(`https://project.trumedianetworks.com/api/nfl/player/${person}`, {
      headers: {
          'tempToken': 'd818a26a-a42d-4fc5-8ab7-44020a0dad5e',
        }
      })
      .then((res) => {
        console.log(res.data);

      if (res.data) {

        setPlayer(res.data);
      }else{
        console.log('error')
      }
    })
    .catch(error => {
      console.log('error', error);
    });

  }, [person]);

  useEffect(() => {
    document.body.addEventListener("keydown", handleClose);
    return function cleanup() {
      document.body.removeEventListener("keydown", handleClose);
    };
  }, []);

  return (
      <div className="modal">
        <div className="modal-content">
            <table>
                <tr>

                </tr>
            </table>

            <button onClick={e=>handleClose(e)} className="button">
              Close
            </button>
            {person}
        </div>
      </div>
  );
};

export default Modal;
